void wait_ms(uint32_t time){
	uint32_t i,j;
	for(i=0; i<time; i++)
		for(j=0; j<255; j++);
}

void PB9_CallBack(){


	if(flag == 0){

		TaskA_flag = 1;
		flag =1;
	}
	else if(flag == 1){

		TaskB_flag = 1;
		flag =0;
	}

}


