int OS_SVC_Set(int a,int b,int SVC_ID)
{
	int result;

	switch(SVC_ID)
	{
	case 1: //add
		__asm("svc #0x01");
		break;
	case 2: //sub
		__asm("svc #0x02");
		break;
	case 3: // MULT
		__asm("svc #0x03");
		break;
	case 4: //PendSV
		SCB->ICSR |= SCB_ICSR_PENDSVSET_Msk;
		break;

	}
	__asm("MOV %[out0],R0": [out0] "=r" (result));
	return result;
}
void OS_SVC_services(int * StackFramePointer)
{
	//OS_SVC_Set Stack -> R0 -> argument 0 = StackFramePointer
	//OS_SVC_Set Stack : R0,R1,R2,R3,R12,LR,PC,xPSR
	unsigned char SVC_Number;
	unsigned int val1,val2;

	SVC_Number =  *((unsigned char*)( ( (unsigned char*)StackFramePointer[6] ) - 2 )) ;

	val1 = StackFramePointer[0];
	val2 = StackFramePointer[1];

	switch(SVC_Number)
	{
	case 1: // ADD
		StackFramePointer[0]= val1 + val2 ;
		break;
	case 2: //SUB
		StackFramePointer[0]= val1 - val2 ;
		break;
	case 3: // MULT
		StackFramePointer[0]= val1 * val2 ;
		break;
	}

	return;




}
__attribute ((naked)) void SVC_Handler(void)
{
	__asm("TST LR,0x4");
	__asm("ITE EQ");
	__asm("MRSEQ R0,MSP");
	__asm("MRSNE R0,PSP");
	__asm("B OS_SVC_services");



}
